import React, { useState } from "react";
import axios from "axios";

export default function DynamicForm({ schema, onSubmit }) {
  const [formData, setFormData] = useState({});
  const [errors, setErrors] = useState({});

  const validate = (field, value) => {
    const err = {};

    // Required check
    if (field.required && (!value || (Array.isArray(value) && value.length === 0))) {
      err[field.name] = field.error || "This field is required.";
      return err;
    }

    // Type-specific checks
    if (field.type === "number" && value !== "") {
      const num = Number(value);
      if (field.min && num < field.min) err[field.name] = field.error || `Minimum value is ${field.min}`;
      if (field.max && num > field.max) err[field.name] = field.error || `Maximum value is ${field.max}`;
    }

    // Validator (Regex)
    if (field.validator && typeof value === "string") {
      try {
        const regex = new RegExp(field.validator);
        if (!regex.test(value)) {
          err[field.name] = field.error || "Invalid format.";
        }
      } catch (e) {
        console.error("Invalid regex in field:", field.name);
      }
    }

    return err;
  };

  const handleChange = async (field, e) => {
    let value = e.target?.value;

    if (field.type === "file") {
      const file = e.target.files[0];
      if (!file) return;

      const form = new FormData();
      form.append("file", file);

      try {
        const res = await axios({
          url: field.data.url,
          method: field.data.method || "POST",
          headers: {
            ...field.data.headers,
            "Content-Type": "multipart/form-data",
          },
          data: form,
        });
        value = res.data.url || "uploaded_file_url";
      } catch (err) {
        console.error("File upload failed:", err);
      }
    }

    setFormData((prev) => ({ ...prev, [field.name]: value }));

    const err = validate(field, value);
    setErrors((prev) => ({ ...prev, ...err }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errs = {};
    schema.forEach((field) => {
      const val = formData[field.name];
      Object.assign(errs, validate(field, val));
    });
    setErrors(errs);

    if (Object.keys(errs).length === 0) {
      onSubmit(formData);
    }
  };

  const renderField = (field) => {
    const value = formData[field.name] || "";

    switch (field.type) {
      case "text":
      case "email":
      case "number":
      case "date":
      case "datetime":
      case "tel":
        return (
          <input
            type={field.type}
            placeholder={field.placeholder}
            value={value}
            onChange={(e) => handleChange(field, e)}
          />
        );

      case "textarea":
        return (
          <textarea
            placeholder={field.placeholder}
            value={value}
            onChange={(e) => handleChange(field, e)}
          />
        );

      case "select":
        return (
          <select
            value={value}
            onChange={(e) => handleChange(field, e)}
          >
            <option value="">-- Select --</option>
            {field.data.map((opt) => (
              <option key={opt.id} value={opt.id}>
                {opt.title}
              </option>
            ))}
          </select>
        );

      case "multiselect":
        return (
          <div>
            {field.data.map((opt) => {
              const selected = value || [];
              return (
                <label key={opt.id} style={{ marginRight: "10px" }}>
                  <input
                    type="checkbox"
                    checked={selected.includes(opt.id)}
                    onChange={() => {
                      const newVal = new Set(selected);
                      newVal.has(opt.id) ? newVal.delete(opt.id) : newVal.add(opt.id);
                      handleChange(field, {
                        target: { value: Array.from(newVal) },
                      });
                    }}
                  />
                  {opt.title}
                </label>
              );
            })}
          </div>
        );

      case "file":
        return <input type="file" onChange={(e) => handleChange(field, e)} />;
case "card":
  return (
    <fieldset className="card">
      <legend>{field.title}</legend>
      <DynamicForm
        schema={field.data}
        onSubmit={(data) => {
          const err = field.data.reduce((acc, f) => {
            return { ...acc, ...validate(f, data[f.name]) };
          }, {});
          if (Object.keys(err).length > 0) {
            setErrors((prev) => ({ ...prev, [field.name]: field.error }));
          } else {
            setErrors((prev) => {
              const newErrs = { ...prev };
              delete newErrs[field.name];
              return newErrs;
            });
            setFormData((prev) => ({ ...prev, [field.name]: data }));
          }
        }}
      />
    </fieldset>
  );


      default:
        return <div>Unsupported field type: {field.type}</div>;
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {schema.map((field) => (
        <div key={field.name} className="form-group">
          <label>{field.title}</label>
          {renderField(field)}
          {errors[field.name] && (
            <div className="error" style={{ color: "red" }}>
              {errors[field.name]}
            </div>
          )}
        </div>
      ))}
      <button type="submit">Submit</button>
    </form>
  );
}
